#!/usr/bin/env python3\nimport sys\nimport os\nfrom pathlib import Path\n\n# Add project root to Python path\nsys.path.insert(0, os.path.dirname(os.path.abspath(__file__)))\n\nprint(\"System Startup Verification\")\nprint(\"=\" * 50)\n\n# Check file integrity\nprint(\"\\nChecking file integrity...\")\nfiles = [\n    'app.py',\n    'web/webrtc1.html', \n    'web/client.js',\n    'pkg/llm/__init__.py',\n    'pkg/rag/__init__.py',\n    'api/llm_api.py',\n]\n\nfor file_path in files:\n    if Path(file_path).exists():\n        print(f\"✅ {file_path}\")\n    else:\n        print(f\"❌ {file_path}\")\n\n# Test imports\nprint(\"\\nTesting module imports...\")\ntry:\n    from pkg.llm import LLMManager\n    print(\"✅ LLM module import success\")\nexcept Exception as e:\n    print(f\"❌ LLM module import failed: {e}\")\n\ntry:\n    from pkg.rag import RAGCore\n    print(\"✅ RAG module import success\")\nexcept Exception as e:\n    print(f\"❌ RAG module import failed: {e}\")\n\ntry:\n    from api.llm_api import register_llm_routes\n    print(\"✅ LLM API import success\")\nexcept Exception as e:\n    print(f\"❌ LLM API import failed: {e}\")\n\n# Test functionality\nprint(\"\\nTesting pkg module functionality...\")\ntry:\n    llm_manager = LLMManager()\n    rag_core = RAGCore(llm_manager)\n    print(\"✅ pkg modules instantiation success\")\nexcept Exception as e:\n    print(f\"❌ pkg modules instantiation failed: {e}\")\n\nprint(\"\\n\" + \"=\" * 50)\nprint(\"Verification completed!\")\nprint(\"Ready to start server: python app.py\")\n